	/**
	 * Tests the quoteName method.
	 *
	 * @return  void
	 *
	 * @since   11.1
	 */
	public function testQuoteName()
	{
		$dbo = $this->getMock(
			'JDatabase',
			array(
				'connected',
				'escape',
				'explain',
				'fetchArray',
				'fetchAssoc',
				'fetchObject',
				'freeResult',
				'getAffectedRows',
				'getCollation',
				'getNumRows',
				'getQuery',
				'getTableColumns',
				'getTableCreate',
				'getTableKeys',
				'getTableList',
				'getVersion',
				'hasUTF',
				'insertId',
				'nameQuote',
				'query',
				'queryBatch',
				'select',
				'setUTF',
				'transactionCommit',
				'transactionRollback',
				'transactionStart',
				'test',
			),
			array(),
			'',
			false
		);

		$dbo->expects(
			$this->any()
		)
		->method('nameQuote')
		->with($this->anything())
		->will(
			$this->returnValue(
				'success'
			)
		);

		$q = new JDatabaseQueryInspector($dbo);

		$this->assertThat(
			$q->quoteName('anything'),
			$this->equalTo('success'),
			'The quoteName method should be a proxy for the JDatabase::escape method.'
		);
	}
