require_once JPATH_PLATFORM.'/joomla/document/raw/raw.php';

/**
 * Test class for JDocumentRAW.
 * Generated by PHPUnit on 2009-10-09 at 13:58:15.
 */
class JDocumentRAWTest extends PHPUnit_Framework_TestCase
{
	/**
	 * @var	JDocumentRAW
	 * @access protected
	 */
	protected $object;

	/**
	 * Sets up the fixture, for example, opens a network connection.
	 * This method is called before a test is executed.
	 *
	 * @access protected
	 */
	protected function setUp()
	{
		$this->object = new JDocumentRAW;
	}

	/**
	 * Tears down the fixture, for example, closes a network connection.
	 * This method is called after a test is executed.
	 *
	 * @access protected
	 */
	protected function tearDown()
	{
	}

	/**
	 * @todo Implement testRender().
	 */
	public function testRender()
	{
		$this->object = new JDocumentRaw;
		JResponse::clearHeaders();

		$this->object->setBuffer('Unit Test Buffer');

		$this->assertThat(
			$this->object->render(),
			$this->equalTo('Unit Test Buffer')
		);

		$headers = JResponse::getHeaders();

		foreach($headers AS $head)
		{
			if ($head['name'] == 'Expires') {
				$this->assertThat(
					$head['value'],
					$this->stringContains('GMT')
				);
			}
		}
	}
}